INSERT INTO `JournalTemplate` VALUES ('d424a6e1-9b66-4134-9f23-03de6768e845',10678,19,1,5,'','2014-11-22 21:59:28','2014-11-22 21:59:28','WELCOME-CONTENT-TEMPLATE','WELCOME-CONTENT-STRUCTURE','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US\" default-locale=\"en_US\"><Name language-id=\"en_US\">Welcome Content Template</Name></root>','','<div class=\"navigation-wrapper\">\n	<header class=\"content-head content-head-liferay-portal\">\n		<hgroup>\n			<h1>$page-title.data</h1>\n\n			<hr />\n		</hgroup>\n\n		<nav>\n			<ul id=\"contentNav\">\n				#foreach($link in $links.siblings)\n					#if ($velocityCount == 1)\n						<li aria-selected=\"true\" class=\"selected\"><a href=\"#pageId$velocityCount\">$link.data</a></li>\n					#else\n						<li><a href=\"#pageId$velocityCount\">$link.data</a></li>\n					#end\n				#end\n			</ul>\n		</nav>\n	</header>\n\n	<div class=\"content-area-wrapper\">\n		#foreach($link in $links.siblings)\n			#if ($velocityCount == 1)\n				<style type=\"text/css\">\n					.content-area {\n						background: url($link.bg-image.data) 100% 0 no-repeat;\n					}\n				</style>\n\n				<div class=\"content-area selected\" data-bannerImage=\"$link.bg-image.data\" id=\"pageId$velocityCount\">\n					$link.data\n\n					<a href=\"$link.action-link-url.data\" id=\"marketplace\">&nbsp;</a>\n				</div>\n			#else\n				<div class=\"content-area\" data-bannerImage=\"$link.bg-image.data\" id=\"pageId$velocityCount\">\n					$link.data\n\n					<a href=\"$link.action-link-url.data\" id=\"marketplace\">&nbsp;</a>\n				</div>\n			#end\n		#end\n	</div>\n</div>\n\n<script charset=\"utf-8\" type=\"text/javascript\">\n	AUI().ready(\n		\'aui-base\',\n		function(A) {\n			var hash = null;\n\n			var selectContent = function(href) {\n				var div = A.one(href);\n\n				if (div) {\n					div.radioClass(\'selected\');\n\n					div.setStyle(\'background\', \'url(\' + div.attr(\'data-bannerImage\')+\') 100% 0 no-repeat\');\n\n					hash = href;\n				}\n			};\n\n			var selectNavItem = function(hash) {\n				if (hash) {\n					A.one(\'#contentNav a[href$=\"\'+ hash +\'\"]\').ancestor().radioClass(\'selected\');\n				}\n			};\n\n			var select = function(hash) {\n				selectContent(hash);\n				selectNavItem(hash);\n			};\n\n			var currentHash = location.hash;\n\n			if (currentHash) {\n				select(currentHash);\n			}\n\n			setInterval(\n				function() {\n					var currentHash = location.hash;\n\n					if (currentHash != hash) {\n						select(currentHash);\n					}\n				},\n				200\n			);\n\n			A.one(\'#contentNav\').delegate(\n				\'click\',\n				function(event) {\n					var a = event.currentTarget;\n\n					a.ancestor().radioClass(\'selected\');\n\n					selectContent(a.attr(\'hash\'));\n				},\n				\'a\'\n			);\n		}\n	);\n</script>','vm',0,0,10679,''),('a1570c98-bb46-4b4d-9eaf-786e936b69b1',55212,55119,1,5,'','2015-03-02 02:14:20','2015-03-02 02:14:20','MAIN-CONTENT','MAIN-CONTENT','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US\" default-locale=\"en_US\"><Name language-id=\"en_US\">Main Content</Name></root>','','#set ($col_count = 0)\n\n#if ($col-one.data != \"\")\n	#set ($col_count = $col_count + 1)\n#end\n\n#if ($col-two.data != \"\")\n	#set ($col_count = $col_count + 1)\n#end\n\n#if ($col-three.data != \"\")\n	#set ($col_count = $col_count + 1)\n#end\n\n<div class=\"page-content\">\n	#if ($title.data != \"\")\n		<h1 class=\"innovation-title\">\n			$title.data\n		</h1>\n\n		<div class=\"divider\"></div>\n	#end\n\n	<div class=\"aui-layout-content content-text\">\n		#if ($col_count == 1 && $col-one.data != \"\")\n			<div class=\"aui-column aui-w100\">\n				<div class=\"aui-column-content\">\n					$col-one.data\n				</div>\n			</div>\n		#end\n\n		#if ($col_count == 2 && $col-one.data != \"\" && $col-two.data != \"\")\n			<div class=\"aui-column aui-column-first aui-w50\">\n				<div class=\"aui-column-content\">\n					$col-one.data\n				</div>\n			</div>\n\n			<div class=\"aui-column aui-column-last aui-w50\">\n				<div